script.adapter:
(function() {
  /js/lib/autolinker.min.js
  Vue.directive('autolinker',(el, binding) => el.innerHTML = Autolinker.link(binding.value, {
    sanitizeHtml: true,
    replaceFn : function (match) {
      switch(match.getType()) {
        case 'url' :
          if(match.getUrl().indexOf(window.location.origin) === 0) {
            return true;
          } else {
            const tag = match.buildTag();
            tag.setAttr('target', '_blank');
            tag.setAttr('rel', 'nofollow noopener noreferrer');
            return tag;
          }
        default :
          return true;
      }
    }
  }));
  return Autolinker;
})();

(function($) {
var oldJQuery = this.jQuery;
this.jQuery = $;

/js/lib/selectize.js

var selectize = this.Selectize;
this.jQuery = oldJQuery;
return selectize;
})($);
